func buildCookie(str string) []*http.Cookie {
	str = strings.TrimSpace(cookies)
	resList := make([]*http.Cookie, 0)
	cookieStrs := strings.Split(str, ";")
	for _, cookieStr := range cookieStrs {
		s := strings.Split(cookieStr, "=")
		resList = append(resList, &http.Cookie{
			Name:  s[0],
			Value: s[1],
		})
	}
	return resList
}
